const e=JSON.parse('{"key":"v-e2fbf5ae","path":"/leetCode/everyday/2023%E5%B9%B48%E6%9C%8826%E6%97%A5.html","title":"2023年8月26日","lang":"zh-CN","frontmatter":{"title":"2023年8月26日","tag":["遍历","双指针"]},"headers":[{"level":3,"title":"遍历","slug":"遍历","link":"#遍历","children":[]}],"git":{"createdTime":1693015935000,"updatedTime":1693015935000,"contributors":[{"name":"liuwenfei","email":"liuwulive@outlook.com","commits":1}]},"readingTime":{"minutes":0.69,"words":208},"filePathRelative":"leetCode/everyday/2023年8月26日.md","localizedDate":"2023年8月26日","excerpt":"<blockquote>\\n<p>LeetCode题目: <a href=\\"https://leetcode.cn/problems/summary-ranges/description/\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">228. 汇总区间</a></p>\\n</blockquote>\\n<h1> 思路</h1>\\n<h3> 遍历</h3>\\n<p>分析题目，要我们找到每段连续增加的序列，记录子序列的起始和终止大小即可</p>\\n<h1> 解题方法</h1>\\n<p>因此可以直接使用一次遍历，每次从当前 i 出发向后查找连续的序列 尾部 j即可，向后查找时，我们使用一个标记位来帮助记录长度是否是1即可</p>"}');export{e as data};
